import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Container,
  Typography,
  TextField,
  Button,
  Grid,
  Paper,
  Card,
  CardContent,
  CardMedia,
  CardActions,
  Chip,
  InputAdornment,
  FormControl,
  Select,
  MenuItem,
  InputLabel,
  CircularProgress,
  Alert} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import FilterListIcon from '@mui/icons-material/FilterList';
import SortIcon from '@mui/icons-material/Sort';
import AnalyticsIcon from '@mui/icons-material/Analytics';
import InfoIcon from '@mui/icons-material/Info';
import CompareArrowsIcon from '@mui/icons-material/CompareArrows';
// Import the API service
import { productAPI } from '../services/api';

function ProductSearchPage() {
  const navigate = useNavigate();

  // State
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [category, setCategory] = useState('All Categories');
  const [sortBy, setSortBy] = useState('Newest First');
  const [page, setPage] = useState(1);
  const [, setTotalPages] = useState(1);
  const [selectedProducts, setSelectedProducts] = useState([]);

  // Fetch products on component mount
  useEffect(() => {
    fetchProducts();
  }, [category, sortBy, page]); // Refetch when these change

  // Function to fetch products from API
  const fetchProducts = async () => {
    setLoading(true);
    setError('');

    try {
      // Prepare params
      const params = {
        page,
        limit: 12, // Number of products per page
        sortBy: getSortParam(sortBy)
      };

      // Add category if not "All Categories"
      if (category !== 'All Categories') {
        params.category = category;
      }

      // Make API call
      const response = await productAPI.getAllProducts(params);

      // Update state with response data
      setProducts(response.data.products || response.data);
      setTotalPages(response.data.totalPages || 1);
    } catch (err) {
      console.error('Error fetching products:', err);
      setError('Failed to load products. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Function to handle search
  const handleSearch = async (e) => {
    e.preventDefault(); // Prevent form submission

    if (!searchTerm.trim()) {
      // If search term is empty, fetch all products
      fetchProducts();
      return;
    }

    setLoading(true);
    setError('');

    try {
      const response = await productAPI.searchProducts(searchTerm.trim());
      setProducts(response.data.products || response.data);
      setTotalPages(response.data.totalPages || 1);
    } catch (err) {
      console.error('Error searching products:', err);
      setError('Failed to search products. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Function to handle category change
  const handleCategoryChange = (e) => {
    setCategory(e.target.value);
    setPage(1); // Reset to first page
  };

  // Function to handle sort change
  const handleSortChange = (e) => {
    setSortBy(e.target.value);
    setPage(1); // Reset to first page
  };

  // Function to get sort parameter based on selected option
  const getSortParam = (sortOption) => {
    switch (sortOption) {
      case 'Newest First':
        return 'createdAt:desc';
      case 'Oldest First':
        return 'createdAt:asc';
      case 'Name A-Z':
        return 'name:asc';
      case 'Name Z-A':
        return 'name:desc';
      case 'Highest Score':
        return 'environmentalScore:desc';
      case 'Lowest Score':
        return 'environmentalScore:asc';
      default:
        return 'createdAt:desc';
    }
  };

  // Function to navigate to product details
  const handleViewDetails = (productId) => {
    navigate(`/products/${productId}`);
  };

  // Function to navigate to analyze page
  const handleAnalyze = (productId) => {
    navigate(`/analyze?productId=${productId}`);
  };

  // Function to toggle product selection for comparison
  const handleToggleSelect = (productId) => {
    if (selectedProducts.includes(productId)) {
      // Remove product
      setSelectedProducts(selectedProducts.filter(id => id !== productId));
    } else {
      // Add product (max 3)
      if (selectedProducts.length < 3) {
        setSelectedProducts([...selectedProducts, productId]);
      } else {
        alert('You can compare up to 3 products at a time');
      }
    }
  };

  // Function to start comparison
  const handleCompare = () => {
    if (selectedProducts.length < 2) {
      alert('Select at least 2 products to compare');
      return;
    }

    navigate(`/compare?products=${selectedProducts.join(',')}`);
  };

  // Function to get image URL or placeholder
  const getImageUrl = (product) => {
    // Check if product has an image URL
    if (product.imageUrl) {
      return product.imageUrl;
    }

    // If no image, return a placeholder based on category
    switch (product.category) {
      case 'Electronics':
        return 'https://via.placeholder.com/300x200?text=Electronics';
      case 'Clothing':
        return 'https://via.placeholder.com/300x200?text=Clothing';
      case 'Home Goods':
        return 'https://via.placeholder.com/300x200?text=Home+Goods';
      default:
        return 'https://via.placeholder.com/300x200?text=Product';
    }
  };

  return (
    <Box className="page-transition">
      {/* Header Banner */}
      <Box
        sx={{
          pt: 6,
          pb: 6,
          backgroundColor: 'primary.main',
          color: 'white',
        }}
        className="eco-gradient-bg"
      >
        <Container maxWidth="lg">
          <Typography
            component="h1"
            variant="h3"
            gutterBottom
            sx={{ fontWeight: 'bold', textAlign: 'center' }}
          >
            Product Search
          </Typography>
          <Typography variant="h6" sx={{ mb: 4, textAlign: 'center' }}>
            Find and compare eco-friendly products
          </Typography>

          {/* Search Bar */}
          <Box
            component="form"
            onSubmit={handleSearch}
            sx={{
              display: 'flex',
              width: '100%',
              maxWidth: 600,
              mx: 'auto',
              borderRadius: 2,
              overflow: 'hidden',
              boxShadow: 3,
            }}
          >
            <TextField
              fullWidth
              placeholder="Search products..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              sx={{
                backgroundColor: 'white',
                borderRadius: 2,
                '& fieldset': {
                  border: 'none',
                },
              }}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon color="primary" />
                  </InputAdornment>
                ),
              }}
            />
            <Button
              type="submit"
              variant="contained"
              sx={{
                borderRadius: '0 8px 8px 0',
                px: 3,
              }}
            >
              SEARCH
            </Button>
          </Box>
        </Container>
      </Box>

      <Container maxWidth="lg" sx={{ py: 4 }}>
        {/* Filters Row */}
        <Box
          sx={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            mb: 3,
            flexWrap: 'wrap',
          }}
        >
          {/* Category Filter */}
          <FormControl sx={{ minWidth: 200, mb: { xs: 2, md: 0 } }}>
            <InputLabel id="category-label">Category</InputLabel>
            <Select
              labelId="category-label"
              value={category}
              label="Category"
              onChange={handleCategoryChange}
              startAdornment={<FilterListIcon sx={{ mr: 1 }} />}
            >
              <MenuItem value="All Categories">All Categories</MenuItem>
              <MenuItem value="Electronics">Electronics</MenuItem>
              <MenuItem value="Clothing">Clothing</MenuItem>
              <MenuItem value="Home Goods">Home Goods</MenuItem>
              <MenuItem value="Food & Beverage">Food & Beverage</MenuItem>
              <MenuItem value="Beauty & Personal Care">Beauty & Personal Care</MenuItem>
            </Select>
          </FormControl>

          {/* Sort By */}
          <FormControl sx={{ minWidth: 200 }}>
            <InputLabel id="sort-by-label">Sort By</InputLabel>
            <Select
              labelId="sort-by-label"
              value={sortBy}
              label="Sort By"
              onChange={handleSortChange}
              startAdornment={<SortIcon sx={{ mr: 1 }} />}
            >
              <MenuItem value="Newest First">Newest First</MenuItem>
              <MenuItem value="Oldest First">Oldest First</MenuItem>
              <MenuItem value="Name A-Z">Name A-Z</MenuItem>
              <MenuItem value="Name Z-A">Name Z-A</MenuItem>
              <MenuItem value="Highest Score">Highest Score</MenuItem>
              <MenuItem value="Lowest Score">Lowest Score</MenuItem>
            </Select>
          </FormControl>
        </Box>

        {/* Selected Products for Comparison */}
        {selectedProducts.length > 0 && (
          <Paper 
            elevation={2} 
            sx={{ 
              p: 2, 
              mb: 3, 
              display: 'flex', 
              justifyContent: 'space-between',
              alignItems: 'center',
              flexWrap: 'wrap'
            }}
          >
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <CompareArrowsIcon sx={{ mr: 1, color: 'primary.main' }} />
              <Typography variant="body1">
                {selectedProducts.length} {selectedProducts.length === 1 ? 'product' : 'products'} selected for comparison
              </Typography>
            </Box>
            <Button
              variant="contained"
              onClick={handleCompare}
              startIcon={<CompareArrowsIcon />}
              sx={{ mt: { xs: 2, sm: 0 } }}
            >
              Compare Products
            </Button>
          </Paper>
        )}

        {/* Error Message */}
        {error && (
          <Alert severity="error" sx={{ mb: 3 }}>
            {error}
          </Alert>
        )}

        {/* Products Header */}
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
          <Typography variant="h5">
            All Products
          </Typography>
          <Typography variant="body2" color="text.secondary">
            Showing {products.length} {products.length === 1 ? 'result' : 'results'}
          </Typography>
        </Box>

        {/* Loading State */}
        {loading && (
          <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
            <CircularProgress />
          </Box>
        )}

        {/* No Products State */}
        {!loading && products.length === 0 && (
          <Paper sx={{ p: 4, textAlign: 'center' }}>
            <Typography variant="h6" gutterBottom>
              No products found
            </Typography>
            <Typography variant="body1" color="text.secondary" paragraph>
              Try adjusting your search or filters
            </Typography>
            <Button
              variant="contained"
              onClick={() => {
                setSearchTerm('');
                setCategory('All Categories');
                setSortBy('Newest First');
                fetchProducts();
              }}
            >
              Reset Filters
            </Button>
          </Paper>
        )}

        {/* Products Grid */}
        <Grid container spacing={3}>
          {!loading && products.map((product) => (
            <Grid item xs={12} sm={6} md={4} key={product._id}>
              <Card 
                elevation={2} 
                sx={{ 
                  height: '100%', 
                  display: 'flex', 
                  flexDirection: 'column',
                  borderRadius: 2,
                  transition: 'transform 0.2s',
                  '&:hover': {
                    transform: 'translateY(-4px)',
                  },
                  border: selectedProducts.includes(product._id) ? 2 : 0,
                  borderColor: 'primary.main',
                }}
              >
                {/* Product Image */}
                <CardMedia
                  component="img"
                  height="200"
                  image={getImageUrl(product)}
                  alt={product.name}
                />

                <CardContent sx={{ flexGrow: 1 }}>
                  {/* Product Title */}
                  <Typography variant="h6" gutterBottom>
                    {product.name}
                  </Typography>

                  {/* Category Badge */}
                  <Chip 
                    label={product.category} 
                    size="small" 
                    sx={{ mb: 2 }} 
                  />

                  {/* Product Details */}
                  <Typography variant="body2" paragraph>
                    <strong>Materials:</strong> {product.materials?.join(', ') || 'Not specified'}
                  </Typography>

                  <Typography variant="body2">
                    <strong>Made in:</strong> {product.manufacturingLocation || 'Not specified'}
                  </Typography>
                </CardContent>

                <CardActions sx={{ p: 2, pt: 0 }}>
                  <Grid container spacing={1}>
                    <Grid item xs={6}>
                      <Button
                        fullWidth
                        variant="outlined"
                        size="small"
                        onClick={() => handleViewDetails(product._id)}
                        startIcon={<InfoIcon />}
                      >
                        DETAILS
                      </Button>
                    </Grid>
                    <Grid item xs={6}>
                      <Button
                        fullWidth
                        variant="contained"
                        size="small"
                        onClick={() => handleAnalyze(product._id)}
                        startIcon={<AnalyticsIcon />}
                      >
                        ANALYZE
                      </Button>
                    </Grid>
                    <Grid item xs={12}>
                      <Button
                        fullWidth
                        variant={selectedProducts.includes(product._id) ? "contained" : "outlined"}
                        size="small"
                        color={selectedProducts.includes(product._id) ? "success" : "primary"}
                        onClick={() => handleToggleSelect(product._id)}
                        startIcon={<CompareArrowsIcon />}
                        sx={{ mt: 1 }}
                      >
                        {selectedProducts.includes(product._id) ? "SELECTED" : "COMPARE"}
                      </Button>
                    </Grid>
                  </Grid>
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
    </Box>
  );
}

export default ProductSearchPage;
