import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Box,
  Container,
  Typography,
  Button,
  Grid,
  Paper,
  Chip,
  Divider,
  CircularProgress,
  Alert,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Card,
  CardContent,
  Tabs,
  Tab,
  Breadcrumbs,
  Link as MuiLink
} from '@mui/material';
import { Link } from 'react-router-dom';;
import PlaceIcon from '@mui/icons-material/Place';
import CategoryIcon from '@mui/icons-material/Category';
import ConstructionIcon from '@mui/icons-material/Construction';
import AnalyticsIcon from '@mui/icons-material/Analytics';
import CompareArrowsIcon from '@mui/icons-material/CompareArrows';
import InfoIcon from '@mui/icons-material/Info';
import NavigateNextIcon from '@mui/icons-material/NavigateNext';
import { productAPI, analysisAPI } from '../services/api';
import LoadingState from '../components/common/LoadingState';
import ErrorState from '../components/common/ErrorState';
import { formatDate } from '../utils/helpers';

// Component to display individual score
const ScoreCircle = ({ score, label, size = 'medium' }) => {
  let scoreClass = '';

  if (score >= 9) scoreClass = 'score-excellent';
  else if (score >= 7) scoreClass = 'score-good';
  else if (score >= 5) scoreClass = 'score-average';
  else if (score >= 3) scoreClass = 'score-poor';
  else scoreClass = 'score-bad';

  const circleSize = size === 'large' ? 100 : size === 'medium' ? 60 : 40;
  const fontSize = size === 'large' ? '2rem' : size === 'medium' ? '1.25rem' : '1rem';

  return (
    <Box sx={{ textAlign: 'center' }}>
      <Box 
        className={`analysis-score-circle ${scoreClass}`}
        sx={{ 
          width: circleSize, 
          height: circleSize, 
          fontSize, 
          mb: 1,
          mx: 'auto'
        }}
      >
        {score}
      </Box>
      <Typography variant={size === 'small' ? 'body2' : 'body1'} component="div">
        {label}
      </Typography>
    </Box>
  );
};

function ProductDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  // State variables
  const [product, setProduct] = useState(null);
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [analyzing, setAnalyzing] = useState(false);
  const [analysisError, setAnalysisError] = useState('');
  const [activeTab, setActiveTab] = useState(0);

  // Fetch product data
  useEffect(() => {
    const fetchProductData = async () => {
      setLoading(true);
      setError('');

      try {
        const response = await productAPI.getProductById(id);
        setProduct(response.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching product:', err);
        setError(err.message || 'Failed to load product details. Please try again.');
        setLoading(false);
      }
    };

    fetchProductData();
  }, [id]);

  // Fetch latest analysis if it exists
  useEffect(() => {
    const fetchAnalysis = async () => {
      try {
        const response = await analysisAPI.getProductAnalysis(id);
        setAnalysis(response.data);
      } catch (err) {
        // Analysis might not exist yet, which is fine
        console.log('No existing analysis found');
      }
    };

    if (!loading && product) {
      fetchAnalysis();
    }
  }, [id, product, loading]);

  // Handle tab change
  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  // Analyze product
  const handleAnalyzeProduct = async () => {
    setAnalyzing(true);
    setAnalysisError('');

    try {
      const response = await analysisAPI.analyzeExistingProduct(id, true);
      setAnalysis(response.data);
      setAnalyzing(false);
    } catch (err) {
      console.error('Error analyzing product:', err);
      setAnalysisError(err.message || 'Failed to analyze product. Please try again.');
      setAnalyzing(false);
    }
  };

  // Loading state
  if (loading) {
    return <LoadingState message="Loading product details..." fullPage />;
  }

  // Error state
  if (error) {
    return (
      <Container maxWidth="md" sx={{ py: 8 }}>
        <ErrorState 
          error={error} 
          fullPage 
          onRetry={() => window.location.reload()}
        />
        <Box sx={{ mt: 3, textAlign: 'center' }}>
          <Button
            variant="contained"
            startIcon={<NavigateNextIcon />}
            onClick={() => navigate('/search')}
          >
            Back to Search
          </Button>
        </Box>
      </Container>
    );
  }

  return (
    <Box className="page-transition">
      <Container maxWidth="lg" sx={{ py: 4 }}>
        {/* Breadcrumbs */}
        <Breadcrumbs 
          separator={<NavigateNextIcon fontSize="small" />} 
          aria-label="breadcrumb"
          sx={{ mb: 3 }}
        >
          <MuiLink component={Link} to="/" color="inherit">
            Home
          </MuiLink>
          <MuiLink component={Link} to="/search" color="inherit">
            Products
          </MuiLink>
          <MuiLink component={Link} to={`/search?category=${product.category}`} color="inherit">
            {product.category}
          </MuiLink>
          <Typography color="text.primary">{product.name}</Typography>
        </Breadcrumbs>

        {/* Product Details */}
        <Grid container spacing={4}>
          {/* Product Image */}
          <Grid item xs={12} md={5}>
            <Paper 
              elevation={2} 
              sx={{ 
                height: '100%', 
                display: 'flex', 
                justifyContent: 'center', 
                alignItems: 'center',
                overflow: 'hidden',
                borderRadius: 2
              }}
            >
              <img 
                src={product.image_url || `https://via.placeholder.com/600x400?text=${encodeURIComponent(product.name)}`} 
                alt={product.name} 
                style={{ width: '100%', height: 'auto' }} 
              />
            </Paper>
          </Grid>

          {/* Product Info */}
          <Grid item xs={12} md={7}>
            <Typography variant="h4" component="h1" gutterBottom>
              {product.name}
            </Typography>

            <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
              <CategoryIcon sx={{ color: 'primary.main', mr: 1 }} />
              <Chip label={product.category} size="medium" />
            </Box>

            <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
              <PlaceIcon sx={{ color: 'primary.main', mr: 1 }} />
              <Typography variant="body1">
                Made in {product.manufacturing_location}
              </Typography>
            </Box>

            <Typography variant="h6" gutterBottom>
              Description
            </Typography>
            <Typography variant="body1" paragraph>
              {product.description}
            </Typography>

            <Typography variant="h6" gutterBottom>
              Materials
            </Typography>
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, mb: 3 }}>
              {product.materials.map((material, index) => (
                <Chip 
                  key={index} 
                  label={material} 
                  size="small" 
                  variant="outlined" 
                  icon={<ConstructionIcon />} 
                />
              ))}
            </Box>

            {product.additional_details && (
              <>
                <Typography variant="h6" gutterBottom>
                  Additional Details
                </Typography>
                <Typography variant="body1" paragraph>
                  {product.additional_details}
                </Typography>
              </>
            )}

            <Box sx={{ display: 'flex', gap: 2, mt: 3 }}>
              <Button 
                variant="contained" 
                color="primary"
                startIcon={<AnalyticsIcon />}
                onClick={handleAnalyzeProduct}
                disabled={analyzing}
              >
                {analyzing ? 'Analyzing...' : analysis ? 'Re-Analyze' : 'Analyze Impact'}
              </Button>
              <Button 
                variant="outlined"
                startIcon={<CompareArrowsIcon />}
                onClick={() => navigate(`/compare?products=${id}`)}
              >
                Compare
              </Button>
              <Button 
                variant="outlined"
                color="secondary"
                onClick={() => navigate(`/products/${id}/edit`)}
              >
                Edit
              </Button>
            </Box>

            {analysisError && (
              <Alert severity="error" sx={{ mt: 2 }}>
                {analysisError}
              </Alert>
            )}
          </Grid>
        </Grid>

        {/* Analysis Results */}
        {(analysis || analyzing) && (
          <Paper elevation={3} sx={{ mt: 6, mb: 4, borderRadius: 2, overflow: 'hidden' }}>
            <Box sx={{ px: 3, py: 2, backgroundColor: 'primary.main', color: 'white' }}>
              <Typography variant="h5">
                Environmental Impact Analysis
              </Typography>
            </Box>

            {analyzing ? (
              <Box sx={{ p: 4, textAlign: 'center' }}>
                <CircularProgress size={60} />
                <Typography variant="h6" sx={{ mt: 2 }}>
                  Analyzing Environmental Impact...
                </Typography>
                <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                  This may take a few moments as our AI evaluates the product details.
                </Typography>
              </Box>
            ) : (
              <Box sx={{ p: 3 }}>
                <Tabs 
                  value={activeTab} 
                  onChange={handleTabChange} 
                  centered
                  sx={{ mb: 3 }}
                >
                  <Tab icon={<AnalyticsIcon />} label="SCORES" />
                  <Tab icon={<InfoIcon />} label="DETAILS" />
                </Tabs>

                {activeTab === 0 && (
                  <Box>
                    {/* Overall Score */}
                    <Box sx={{ textAlign: 'center', mb: 4 }}>
                      <Typography variant="h5" gutterBottom>
                        Overall Environmental Score
                      </Typography>
                      <ScoreCircle 
                        score={analysis.scores.overall} 
                        label="Overall" 
                        size="large" 
                      />
                    </Box>

                    <Divider sx={{ my: 3 }} />

                    {/* Individual Scores */}
                    <Grid container spacing={4} sx={{ mt: 1 }}>
                      <Grid item xs={12} md={4}>
                        <ScoreCircle 
                          score={analysis.scores.carbon} 
                          label="Carbon Footprint" 
                        />
                      </Grid>
                      <Grid item xs={12} md={4}>
                        <ScoreCircle 
                          score={analysis.scores.water} 
                          label="Water Usage" 
                        />
                      </Grid>
                      <Grid item xs={12} md={4}>
                        <ScoreCircle 
                          score={analysis.scores.resources} 
                          label="Resource Consumption" 
                        />
                      </Grid>
                    </Grid>
                  </Box>
                )}

                {activeTab === 1 && (
                  <Box>
                    <Card variant="outlined" sx={{ mb: 3 }}>
                      <CardContent>
                        <Typography variant="h6" gutterBottom>
                          Analysis Explanation
                        </Typography>
                        <Typography variant="body1">
                          {analysis.explanation}
                        </Typography>
                      </CardContent>
                    </Card>

                    <Card variant="outlined">
                      <CardContent>
                        <Typography variant="h6" gutterBottom>
                          Improvement Suggestions
                        </Typography>
                        <List>
                          {analysis.suggestions.split(/\d+\./).filter(s => s.trim()).map((suggestion, index) => (
                            <ListItem key={index}>
                              <ListItemIcon>
                                <EcoIcon color="primary" />
                              </ListItemIcon>
                              <ListItemText primary={suggestion.trim()} />
                            </ListItem>
                          ))}
                        </List>
                      </CardContent>
                    </Card>

                    <Box sx={{ mt: 3, textAlign: 'right' }}>
                      <Typography variant="body2" color="text.secondary">
                        Analysis performed: {formatDate(analysis.createdAt)}
                      </Typography>
                    </Box>
                  </Box>
                )}
              </Box>
            )}
          </Paper>
        )}

        {/* Similar Products */}
        <Box sx={{ mt: 6, mb: 4 }}>
          <Typography variant="h5" gutterBottom>
            Similar Products
          </Typography>
          <Typography variant="body1" paragraph color="text.secondary">
            Compare with other products in the same category
          </Typography>

          <Grid container spacing={3}>
            {/* In a real implementation, this would be fetched from the API */}
            {[1, 2, 3].map((num) => (
              <Grid item key={num} xs={12} sm={6} md={4}>
                <Paper 
                  elevation={2} 
                  sx={{ 
                    p: 2, 
                    display: 'flex', 
                    flexDirection: 'column',
                    alignItems: 'center',
                    cursor: 'pointer',
                    transition: 'transform 0.3s ease, box-shadow 0.3s ease',
                    '&:hover': {
                      transform: 'translateY(-5px)',
                      boxShadow: 4
                    }
                  }}
                  onClick={() => navigate(`/search?category=${product.category}`)}
                >
                  <img 
                    src={`https://via.placeholder.com/300x200?text=Similar+Product+${num}`}
                    alt={`Similar Product ${num}`}
                    style={{ width: '100%', height: 'auto', marginBottom: '12px' }} 
                  />
                  <Typography variant="h6" align="center">
                    Similar Product {num}
                  </Typography>
                  <Chip 
                    label={product.category} 
                    size="small" 
                    sx={{ mt: 1 }} 
                  />
                  <Button
                    variant="text"
                    color="primary"
                    startIcon={<CompareArrowsIcon />}
                    sx={{ mt: 2 }}
                    onClick={(e) => {
                      e.stopPropagation();
                      navigate(`/compare?products=${id},similar-${num}`);
                    }}
                  >
                    Compare
                  </Button>
                </Paper>
              </Grid>
            ))}
          </Grid>
        </Box>
      </Container>
    </Box>
  );
}

export default ProductDetailPage;
