import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  Box,
  Container,
  Typography,
  TextField,
  Button,
  Grid,
  Card,
  CardContent,
  CardMedia,
  CardActions,
  Chip,
  InputAdornment,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
  Pagination,
  CircularProgress,
  Alert,
  Checkbox
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import FilterListIcon from '@mui/icons-material/FilterList';
import SortIcon from '@mui/icons-material/Sort';
import EcoIcon from '../components/icons/EcoIcon';
import AnalyticsIcon from '@mui/icons-material/Analytics';
import CompareArrowsIcon from '@mui/icons-material/CompareArrows';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
// Import the API service
import { productAPI } from '../services/api';

// Define product categories for filtering
const productCategories = [
  'All Categories',
  'Electronics',
  'Clothing',
  'Food',
  'Home Goods',
  'Beauty',
  'Automotive',
  'Furniture'
];

function ProductSearchPage() {
  const navigate = useNavigate();
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);

  // State for search and filters
  const [searchQuery, setSearchQuery] = useState(queryParams.get('q') || '');
  const [category, setCategory] = useState('All Categories');
  const [sortBy, setSortBy] = useState('latest');
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [selectedProducts, setSelectedProducts] = useState([]);

  // Mock initial products for display purposes - wrapped in useMemo to prevent recreation on every render
  const initialProducts = useMemo(() => [
    {
      _id: '1',
      name: 'Eco-Friendly Smartphone',
      category: 'Electronics',
      description: 'Made with recycled materials and designed for easy repair and recycling at end of life.',
      materials: ['Recycled Aluminum', 'Recycled Plastic', 'Glass'],
      manufacturingLocation: 'Germany',
      imageUrl: 'https://via.placeholder.com/300x200?text=Eco+Phone'
    },
    {
      _id: '2',
      name: 'Organic Cotton T-Shirt',
      category: 'Clothing',
      description: 'Made with 100% organic cotton grown without harmful pesticides or synthetic fertilizers.',
      materials: ['Organic Cotton'],
      manufacturingLocation: 'Portugal',
      imageUrl: 'https://via.placeholder.com/300x200?text=Organic+Shirt'
    },
    {
      _id: '3',
      name: 'Bamboo Kitchen Utensils',
      category: 'Home Goods',
      description: 'Sustainable bamboo kitchen utensils that are biodegradable and renewable.',
      materials: ['Bamboo'],
      manufacturingLocation: 'Vietnam',
      imageUrl: 'https://via.placeholder.com/300x200?text=Bamboo+Utensils'
    },
    {
      _id: '4',
      name: 'Solar-Powered Power Bank',
      category: 'Electronics',
      description: 'Charge your devices using clean solar energy. Includes recycled components.',
      materials: ['Recycled Plastic', 'Silicon', 'Lithium Battery'],
      manufacturingLocation: 'China',
      imageUrl: 'https://via.placeholder.com/300x200?text=Solar+Power+Bank'
    },
    {
      _id: '5',
      name: 'Plant-Based Laundry Detergent',
      category: 'Home Goods',
      description: 'Biodegradable laundry detergent made from plant-derived ingredients.',
      materials: ['Plant Extracts', 'Natural Enzymes'],
      manufacturingLocation: 'USA',
      imageUrl: 'https://via.placeholder.com/300x200?text=Eco+Detergent'
    },
    {
      _id: '6',
      name: 'Recycled Paper Notebook',
      category: 'Home Goods',
      description: '100% recycled paper notebook with vegetable-based ink printing.',
      materials: ['Recycled Paper', 'Vegetable Ink'],
      manufacturingLocation: 'Canada',
      imageUrl: 'https://via.placeholder.com/300x200?text=Recycled+Notebook'
    }
  ], []); // Empty dependency array means this won't change

  // Function to get sort parameter based on selected option
  const getSortParam = (sortOption) => {
    switch (sortOption) {
      case 'latest':
        return 'createdAt:desc';
      case 'name_asc':
        return 'name:asc';
      case 'name_desc':
        return 'name:desc';
      default:
        return 'createdAt:desc';
    }
  };

  // Function to fetch products from API - wrapped in useCallback to prevent recreation on every render
  const fetchProducts = useCallback(async () => {
    // For initial development/testing, use the mock data
    if (process.env.REACT_APP_USE_MOCK_DATA === 'true') {
      let filteredProducts = [...initialProducts];

      // Apply search filter
      if (searchQuery) {
        const query = searchQuery.toLowerCase();
        filteredProducts = filteredProducts.filter(p => 
          p.name.toLowerCase().includes(query) || 
          p.description.toLowerCase().includes(query) ||
          p.materials.some(m => m.toLowerCase().includes(query))
        );
      }

      // Apply category filter
      if (category !== 'All Categories') {
        filteredProducts = filteredProducts.filter(p => p.category === category);
      }

      // Apply sorting
      if (sortBy === 'name_asc') {
        filteredProducts.sort((a, b) => a.name.localeCompare(b.name));
      } else if (sortBy === 'name_desc') {
        filteredProducts.sort((a, b) => b.name.localeCompare(a.name));
      }

      setProducts(filteredProducts);
      setTotalPages(Math.ceil(filteredProducts.length / 6));
      return;
    }

    // Real API implementation
    setLoading(true);
    setError('');

    try {
      let response;

      if (searchQuery) {
        response = await productAPI.searchProducts(searchQuery);
      } else {
        response = await productAPI.getAllProducts({
          category: category !== 'All Categories' ? category : undefined,
          sort: getSortParam(sortBy),
          page,
          limit: 6
        });
      }

      setProducts(response.data.products || response.data);
      setTotalPages(response.data.totalPages || Math.ceil((response.data.products || response.data).length / 6));
    } catch (err) {
      console.error('Error fetching products:', err);
      setError('Failed to load products. Please try again.');
      // Fallback to initial products if API fails
      setProducts(initialProducts);
    } finally {
      setLoading(false);
    }
  }, [searchQuery, category, sortBy, page, initialProducts]); // Include all dependencies

  // Effect to load products when filters change
  useEffect(() => {
    fetchProducts();
  }, [fetchProducts]); // fetchProducts is now memoized with useCallback

  // Update URL when search changes
  useEffect(() => {
    if (searchQuery) {
      const params = new URLSearchParams();
      params.set('q', searchQuery);
      navigate({ search: params.toString() });
    } else if (location.search) {
      // Clear search params if searchQuery is empty
      navigate({ search: '' });
    }
  }, [searchQuery, navigate, location.search]);

  // Handle search submission
  const handleSearch = (e) => {
    e.preventDefault();
    setPage(1); // Reset to first page on new search
    fetchProducts(); // Explicitly call fetchProducts
  };

  // Handle product selection for comparison
  const handleToggleSelection = (productId) => {
    setSelectedProducts(prevSelected => {
      if (prevSelected.includes(productId)) {
        // Remove the product if already selected
        return prevSelected.filter(id => id !== productId);
      } else {
        // Add the product if not already selected and fewer than 3 products are selected
        if (prevSelected.length < 3) {
          return [...prevSelected, productId];
        } else {
          alert('You can only compare up to 3 products at a time');
          return prevSelected;
        }
      }
    });
  };

  // Navigate to comparison page
  const handleCompare = () => {
    if (selectedProducts.length > 1) {
      navigate(`/compare?products=${selectedProducts.join(',')}`);
    } else {
      alert('Please select at least 2 products to compare');
    }
  };

  // Render product card
  const renderProductCard = (product) => {
    const isSelected = selectedProducts.includes(product._id);

    return (
      <Card 
        sx={{
          height: '100%', 
          display: 'flex', 
          flexDirection: 'column',
          border: isSelected ? '2px solid #2e7d32' : '1px solid #e0e0e0',
          position: 'relative',
          transition: 'all 0.3s ease',
          '&:hover': {
            boxShadow: '0 4px 8px rgba(0,0,0,0.1)'
          }
        }}
      >
        {/* Selection Checkbox - Always visible */}
        <Checkbox
          checked={isSelected}
          onChange={() => handleToggleSelection(product._id)}
          sx={{ 
            position: 'absolute',
            top: 5,
            right: 5,
            backgroundColor: 'rgba(255,255,255,0.8)',
            borderRadius: '50%',
            padding: '2px',
            '&.Mui-checked': {
              color: '#2e7d32',
            }
          }}
          icon={<CompareArrowsIcon />}
          checkedIcon={<CheckCircleIcon />}
        />

        <CardMedia
          component="img"
          height="140"
          image={product.imageUrl || `https://via.placeholder.com/300x200?text=${encodeURIComponent(product.name)}`}
          alt={product.name}
          sx={{ objectFit: 'cover' }}
        />

        <CardContent sx={{ flexGrow: 1, pt: 2 }}>
          <Typography variant="h6" component="div" gutterBottom>
            {product.name}
          </Typography>

          <Chip
            label={product.category}
            size="small"
            sx={{ mb: 1 }}
          />

          <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
            <strong>Materials:</strong> {Array.isArray(product.materials) ? product.materials.join(', ') : 'Not specified'}
          </Typography>

          <Typography variant="body2" color="text.secondary">
            <strong>Made in:</strong> {product.manufacturingLocation || 'Not specified'}
          </Typography>
        </CardContent>

        <CardActions sx={{ p: 2, pt: 0 }}>
          <Button 
            size="small"
            variant="outlined"
            startIcon={<EcoIcon />}
            onClick={() => navigate(`/products/${product._id}`)}
          >
            DETAILS
          </Button>

          <Button 
            size="small"
            variant="contained"
            color="primary"
            startIcon={<AnalyticsIcon />}
            onClick={() => navigate(`/analyze?productId=${product._id}`)}
          >
            ANALYZE
          </Button>
        </CardActions>
      </Card>
    );
  };

  return (
    <Box className="page-transition">
      {/* Hero Section */}
      <Box
        sx={{
          pt: 6,
          pb: 6,
          backgroundColor: 'primary.main',
          color: 'white',
        }}
        className="eco-gradient-bg"
      >
        <Container maxWidth="md">
          <Typography
            component="h1"
            variant="h3"
            gutterBottom
            sx={{ fontWeight: 'bold', textAlign: 'center' }}
          >
            Product Search
          </Typography>
          <Typography variant="h6" sx={{ mb: 4, textAlign: 'center' }}>
            Find and compare eco-friendly products
          </Typography>

          {/* Search Form */}
          <Box 
            component="form" 
            onSubmit={handleSearch}
            sx={{ 
              display: 'flex', 
              alignItems: 'center',
              bgcolor: 'white',
              borderRadius: 2,
              p: 0.5,
              maxWidth: 600,
              mx: 'auto'
            }}
          >
            <TextField
              fullWidth
              variant="outlined"
              placeholder="Search products..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              sx={{ 
                "& .MuiOutlinedInput-root": {
                  "& fieldset": { border: "none" },
                }
              }}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon />
                  </InputAdornment>
                ),
              }}
            />
            <Button 
              type="submit" 
              variant="contained"
              size="large"
              sx={{ ml: 1 }}
            >
              Search
            </Button>
          </Box>
        </Container>
      </Box>

      {/* Main Content */}
      <Container sx={{ py: 6 }} maxWidth="lg">
        {/* Filters and Compare Button Row */}
        <Box 
          sx={{ 
            display: 'flex', 
            flexDirection: { xs: 'column', md: 'row' }, 
            justifyContent: 'space-between',
            alignItems: { xs: 'stretch', md: 'center' },
            mb: 4,
            gap: 2
          }}
        >
          <Box sx={{ display: 'flex', gap: 2, flexDirection: { xs: 'column', sm: 'row' } }}>
            <FormControl variant="outlined" sx={{ minWidth: 150 }}>
              <InputLabel id="category-label">Category</InputLabel>
              <Select
                labelId="category-label"
                value={category}
                onChange={(e) => setCategory(e.target.value)}
                label="Category"
                startAdornment={
                  <InputAdornment position="start">
                    <FilterListIcon fontSize="small" />
                  </InputAdornment>
                }
              >
                {productCategories.map((cat) => (
                  <MenuItem key={cat} value={cat}>
                    {cat}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            <FormControl variant="outlined" sx={{ minWidth: 150 }}>
              <InputLabel id="sort-label">Sort By</InputLabel>
              <Select
                labelId="sort-label"
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
                label="Sort By"
                startAdornment={
                  <InputAdornment position="start">
                    <SortIcon fontSize="small" />
                  </InputAdornment>
                }
              >
                <MenuItem value="latest">Newest First</MenuItem>
                <MenuItem value="name_asc">Name (A-Z)</MenuItem>
                <MenuItem value="name_desc">Name (Z-A)</MenuItem>
              </Select>
            </FormControl>
          </Box>

          {/* Always show compare button, but disable if fewer than 2 products selected */}
          <Button
            variant="contained"
            color="primary"
            startIcon={<CompareArrowsIcon />}
            onClick={handleCompare}
            disabled={selectedProducts.length < 2}
            sx={{ 
              minWidth: '180px',
              visibility: selectedProducts.length > 0 ? 'visible' : 'visible'
            }}
          >
            COMPARE ({selectedProducts.length})
          </Button>
        </Box>

        {/* Results count and info */}
        <Box sx={{ mb: 3, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <Typography variant="body1">
            {searchQuery ? 
              `Search results for "${searchQuery}"` : 
              category !== 'All Categories' ? 
                `Showing ${category} products` : 
                'All Products'
            }
          </Typography>
          <Typography variant="body2" color="text.secondary">
            Showing {products.length} results
          </Typography>
        </Box>

        {/* Selection Instruction */}
        {selectedProducts.length > 0 && (
          <Alert 
            severity="info" 
            sx={{ mb: 3 }}
          >
            <Typography variant="body2">
              {selectedProducts.length === 1 
                ? "You've selected 1 product. Select at least one more to compare." 
                : `You've selected ${selectedProducts.length} products. Click COMPARE to view side-by-side.`}
            </Typography>
          </Alert>
        )}

        {/* Error Message */}
        {error && (
          <Alert severity="error" sx={{ mb: 3 }}>
            {error}
          </Alert>
        )}

        {/* Loading Indicator */}
        {loading && (
          <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
            <CircularProgress />
          </Box>
        )}

        {/* No Results */}
        {!loading && products.length === 0 && (
          <Box sx={{ textAlign: 'center', py: 6 }}>
            <Typography variant="h5" gutterBottom>
              No products found
            </Typography>
            <Typography variant="body1" color="text.secondary">
              Try adjusting your search or filters to find what you're looking for.
            </Typography>
            <Button 
              variant="contained" 
              sx={{ mt: 3 }}
              onClick={() => {
                setSearchQuery('');
                setCategory('All Categories');
                setSortBy('latest');
              }}
            >
              Clear Filters
            </Button>
          </Box>
        )}

        {/* Product Grid */}
        <Grid container spacing={3}>
          {products.map((product) => (
            <Grid item key={product._id} xs={12} sm={6} md={4}>
              {renderProductCard(product)}
            </Grid>
          ))}
        </Grid>

        {/* Pagination */}
        {totalPages > 1 && (
          <Box sx={{ display: 'flex', justifyContent: 'center', mt: 6 }}>
            <Pagination 
              count={totalPages} 
              page={page} 
              onChange={(e, value) => setPage(value)}
              color="primary"
              size="large"
            />
          </Box>
        )}
      </Container>
    </Box>
  );
}

export default ProductSearchPage;
